import os
import tkinter as tk
from tkinter import ttk, messagebox
import subprocess
from PIL import Image, ImageTk
from concurrent.futures import ThreadPoolExecutor
import base64
from io import BytesIO 

# Author： 永恒之锋-Gu0st
version="V2.2"
# 获取指定目录下的所有批处理文件（.bat文件）
def list_batch_files(d):
    return [os.path.join(r, f) for r, _, fs in os.walk(d) for f in fs if f.endswith(".bat")]

# 运行指定的批处理文件
def run_batch_file(f):
    try:
        print(f"正在运行 {os.path.basename(f)}...")
        subprocess.Popen(f, creationflags=subprocess.CREATE_NO_WINDOW)
    except Exception as e:
        print(f"运行 {os.path.basename(f)} 时出错: {str(e)}")

# 刷新文件列表
def refresh_files():
    files = list_batch_files(os.getcwd().replace('\\', '/'))
    file_buttons.destroy()  # 直接销毁原有的文件按钮Frame
    create_file_buttons(files)  # 重新创建文件按钮

# 创建文件按钮
def create_file_buttons(files):
    global file_buttons
    file_buttons = tk.Frame(root)
    file_buttons.pack(fill=tk.BOTH, expand=True)

    cols, fpr = 5, (len(files) + 4) // 5

    for i, f in enumerate(files):
        ttk.Button(file_buttons, text=os.path.basename(f), command=lambda f=f: run_batch_file(f)).grid(row=i // cols, column=i % cols, padx=10, pady=5, sticky="nsew")

    for j in range(cols):
        file_buttons.columnconfigure(j, weight=1)

    for k in range(fpr):
        file_buttons.rowconfigure(k, weight=1)

# 显示帮助信息
def show_help():
    help_text = """
    欢迎使用蝙蝠(BatBox){ver}工具箱！
    
    注意：
    1. 将需要运行的 .bat 文件放置在工具箱的目录下或其子目录中。
    2. 在工具箱界面中，您可以点击按钮来运行对应的 .bat 文件。
    3. 如需刷新文件列表，可点击菜单中的“刷新”选项。
    4. 如需要添加作者，请点击作者按钮，扫码添加。
    5. 以后还会对该工具的GUI界面进行美化，欢迎提出宝贵的建议。
    """.format(ver=version)
    help_window = tk.Toplevel(root)
    help_window.title("帮助")
    help_label = tk.Label(help_window, text=help_text, padx=10, pady=10, justify="left")
    help_label.pack()

# 显示作者信息
def author():
    author_window = tk.Toplevel(root)
    author_window.title("作者")

    base_author = b''
    # 使用base64解码图片  
    img_data = base64.b64decode(base_author)  
    image = Image.open(BytesIO(img_data))  
    photo = ImageTk.PhotoImage(image)  
  
    # 创建一个Label来显示图片  
    image_label = tk.Label(author_window, image=photo)  
    image_label.image = photo  # 保持对photo的引用，防止被垃圾回收  
    image_label.pack()  
    
