# 创建主窗口
root = tk.Tk()
root.title("蝙蝠(BatBox)"+version+"工具箱")

# 硬编码icon
icon_img = b''
icon_img = base64.b64decode(icon_img)
icon_img = ImageTk.PhotoImage(data=icon_img)
root.tk.call('wm', 'iconphoto', root._w, icon_img)

menu_bar = tk.Menu(root)
root.config(menu=menu_bar)

# 创建菜单栏选项
menu_bar.add_command(label="刷新", command=refresh_files)
menu_bar.add_command(label="帮助", command=show_help)
menu_bar.add_command(label="作者", command=author)
menu_bar.add_command(label="关于", command=about)
menu_bar.add_command(label="更新记录", command=update_records)

# 使用线程池并行处理文件列表
with ThreadPoolExecutor() as executor:
    files = list_batch_files(os.getcwd().replace('\\', '/'))
    create_file_buttons(files)

# 进入主事件循环
root.mainloop()

